mapper.select:
select car_order_id as carOrderId ,user_no as userNo ,open_id as openId ,addr_from as addrFrom ,addr_to as addrTo ,substr(depart_Time, 6, 11) as departTime ,order_phone as orderPhone ,order_name as orderName ,substr(order_time, 6, 11) as orderTime ,order_status as orderStatus ,data_state as dataState from car_order_info where 1 = 1
and car_order_id = #{carOrderId}
and open_id = #{openId}
and order_status in ( ${orderStatus} )
and data_state = '0'

mapper.update:
update car_order_info
set
  
    car_order_id       = #{carOrderId}
  
  
   ,user_no            = #{userNo}
  
  
    ,open_id           = #{openId}
  
  
    ,addr_from         = #{addrFrom}
  
  
    ,addr_to           = #{addrTo}
  
  
    ,depart_time       = #{departTime}
  
  
    ,order_phone       = #{orderPhone}
  
  
    ,order_name        = #{orderName}
  
  
    ,order_time        = #{orderTime}
  
  
    ,order_status      = #{orderStatus}
  
  
    ,data_state        = #{dataState}
  
where
  1 = 1
  
    and car_order_id = #{carOrderId}
