mapper.update:
    update user_info
    set
      1 = 1
      
        ,user_no          = #{userNo}
      
      
        ,open_id          = #{openId}
      
      
        ,nick_name        = #{nickName}
      
      
        ,sex              = #{sex}
      
      
        ,country          = #{country}
      
      
        ,province         = #{province}
      
      
        ,city             = #{city}
      
      
        ,head_img_url     = #{headImgUrl}
      

        ,privilege_list   = ''

      
        ,name             = #{name}
      
      
        ,phone            = #{phone}
      
      
        ,addr             = #{addr}
      
      
        ,id               = #{id}
      

        ,create_time      = now()
    where
    1 = 1
    
      and user_no = #{userNo}
    
    
      and open_id = #{openId}
